import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Best_sellers } from 'models/model';


@Component({
  selector: 'app-best-sellers',
  templateUrl: './best-sellers.component.html',
  styleUrls: ['./best-sellers.component.css']
})
export class Best_SellersComponent {
  best_sellers: Best_sellers[];

  constructor(private router:Router){
    this.best_sellers=this.router.getCurrentNavigation().extras.state as Best_sellers[];
  }
}

<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Anchor Link</title>
<style>
  /* Stili per il testo cliccabile */
  .cliccabile {
    cursor: pointer;
    text-decoration: underline;
    color: blue;
  }
</style>
</head>
<body>

<p>Ciao, qui puoi scegliere i best sellers</p>

<!-- Parole cliccabili -->
<p>Anni '80' <span class="cliccabile" onclick="scrollToAnchor('sezioneMario')">
    <div>'80</div>


</span></p>

<!-- Sezione di destinazione -->
<div id="sezioneMario">
  <h2>Sezione Mario</h2>
  <p>Questo è il paragrafo relativo a Mario.</p>
</div>

<script>
function scrollToAnchor(anchor) {
  // Trova l'elemento con l'id corrispondente all'ancora
  var targetElement = document.getElementById(anchor);

  // Controlla se l'elemento esiste
  if (targetElement) {
    // Calcola la posizione dell'elemento rispetto all'inizio della pagina
    var targetOffset = targetElement.offsetTop;

    // Esegui lo scorrimento verso l'ancora
    window.scrollTo({
      top: targetOffset,
      behavior: 'smooth' // Esegui lo scorrimento in modo fluido
    });
  }
}
</script>

</body>
</html>
